import React, {Component} from 'react';
import Grid from "./Grid";
import Controller from "./Controller";

interface AppState {
    location: [number, number] // x, y
    direction: string
    canvasSize: number
    screen: string
}

class App extends Component<{}, AppState> {
    constructor(props: any) {
        super(props);
        this.state = {
            location: [400, 400],
            canvasSize: 800,
            direction: "front",
            screen: "start"
        };
    }

    updateLocation = (newLocation: [number, number], newDirection: string) => {
        this.setState({
            location: newLocation,
            direction: newDirection
        });
    }

    startScreen = () => {
        this.setState({
           screen: "world"
        });
    }


    render() {
        const canvas_size = 800;
        if (this.state.screen === "world") {
            return (
                <div>
                    <Grid width={canvas_size} height={canvas_size} location={this.state.location}
                          direction={this.state.direction}/>
                    <Controller location={this.state.location} onChange={this.updateLocation}/>

                </div>
            );
        } else if (this.state.screen === "battle") {
            return (
                <div>
                    <h1>WIP</h1>
                </div>
            )
        } else { // default start screen
            return (
                <div>
                    <h1>Welcome to Pokemon World</h1>
                    <h2>Please enter your name</h2>
                    <button onClick={this.startScreen}>Start</button>
                </div>
            )
        }
    }

}

export default App;
