import React, {Component} from 'react';
import Grid from "./Grid";

interface AppState {
    location: [number, number] // x, y
}

class App extends Component<{}, AppState> {
    handleMovement = (event: { key: string; }) => {
        if(event.key === 'w'){ // go up 1
            this.setState({
                location: [this.state.location[0] ,this.state.location[1] + 1]
            });
        }
        else if (event.key === 's') { // go right one
            this.setState({
                location: [this.state.location[0],this.state.location[1] - 1]
            });
        }
        else if (event.key === 'd') { // go left 1
            this.setState({
                location: [this.state.location[0] + 1, this.state.location[1]]
            });
        }
        else if (event.key === 'a') { // go down 1
            this.setState({
                location: [this.state.location[0] - 1, this.state.location[1]]
            });
        }
    }

    render() {
        const canvas_size = 800;
        return (
            <div>
                <h1>Pokemon World</h1>
                <Grid width={canvas_size} height={canvas_size} />
                <textarea onKeyPress={this.handleMovement}/>
            </div>
        );
    }

}

export default App;
