import React, {Component} from 'react';
import Grid from "./Grid";
import Controller from "./Controller";

interface AppState {
    location: [number, number] // x, y
    direction: string
    canvasSize: number
}

class App extends Component<{}, AppState> {
    constructor(props: any) {
        super(props);
        this.state = {
            location: [400, 400],
            canvasSize: 800,
            direction: "front"
        };
    }

    updateLocation = (newLocation: [number, number], newDirection: string) => {
        this.setState({
            location: newLocation,
            direction: newDirection
        });
    }


    render() {
        const canvas_size = 800;
        return (
            <div>
                <h1>Pokemon World</h1>
                <Grid width={canvas_size} height={canvas_size} location={this.state.location} direction={this.state.direction}/>
                <Controller location={this.state.location} onChange={this.updateLocation}/>

            </div>
        );
    }

}

export default App;
