import React, {Component} from 'react';

interface ContollerState {
    currentText: string;
}

interface ControllerProps {
    location: [number, number];
    onChange(newLocation: [number, number], newDirection: string): void;
}

class Controller extends Component<ControllerProps, ContollerState> {
    constructor(props: ControllerProps) {
        super(props);
        this.state = {
            currentText: ""
        }
    }
    // since this is being handled by the textbook I should probably have it be
    // it's only file that can also handle the fight
    handleMovement = (event: { key: string; }) => {
        if(event.key === 'w'){ // go up 1
            this.props.onChange([this.props.location[0] ,this.props.location[1] - 10], "back");
        }
        else if (event.key === 's') { // go down one
            this.props.onChange([this.props.location[0] ,this.props.location[1] + 10], "front");
        }
        else if (event.key === 'a') { // go left 1
            this.props.onChange([this.props.location[0] - 10 ,this.props.location[1]], "left");
        }
        else if (event.key === 'd') { // go right 1
            this.props.onChange([this.props.location[0] + 10 ,this.props.location[1]], "right");
        }
        this.setState({
            currentText: ""
        })
    }

    render() {
        return (
            <textarea onKeyPress={this.handleMovement} value={this.state.currentText}/>
        );
    }
}

export default Controller;
