import React, {Component} from 'react';
import Grid from "./Grid";

interface AppState {
    location: [number, number] // x, y
    direction: string
    canvasSize: number
}

class App extends Component<{}, AppState> {
    constructor(props: any) {
        super(props);
        this.state = {
            location: [400, 400],
            canvasSize: 800
        };
    }

    handleMovement = (event: { key: string; }) => {
        if(event.key === 'w'){ // go up 1
            if (this.state.location)
            this.setState({
                location: [this.state.location[0] ,this.state.location[1] - 10],
                direction: "back"
            });
        }
        else if (event.key === 's') { // go down one
            this.setState({
                location: [this.state.location[0],this.state.location[1] + 10],
                direction: "front"
            });
        }
        else if (event.key === 'a') { // go left 1
            this.setState({
                location: [this.state.location[0] - 10, this.state.location[1]],
                direction: "left"
            });
        }
        else if (event.key === 'd') { // go right 1
            this.setState({
                location: [this.state.location[0] + 10, this.state.location[1]],
                direction: "right"
            });
        }
    }

    render() {
        const canvas_size = 800;
        return (
            <div>
                <h1>Pokemon World</h1>
                <Grid width={canvas_size} height={canvas_size} location={this.state.location} direction={this.state.direction}/>
                <textarea onKeyPress={this.handleMovement}/>
            </div>
        );
    }

}

export default App;
